import React from "react";
import "./alert.css";

const Alert = ({ type, text }) => {
  return (
    <div class="content absolute top-[75px] px-5">
      <div
        class={`alert ${
          type === "SUCCESS"
            ? "alert-success"
            : type === "ERROR"
            ? "alert-error"
            : "alert-info"
        } alert-white rounded`}
      >
        <button
          type="button"
          class="close"
          data-dismiss="alert"
          aria-hidden="true"
        >
          Ã—
        </button>
        <div class="icon">
          <i class="fa fa-check"></i>
        </div>
        <strong>Success!</strong> Changes has been
        saved successfully!
      </div>
    </div>
  );
};

export default Alert;
