import {
  SET_CATEGORY,
  SET_SORT_BY,
  set
} from "../constants/filterConstants";

export const setCategory = (category) => {
  return {
    type: SET_CATEGORY,
    payload: category,
  };
};

export const setSortBy = (sortBy) => {
  return {
    type: SET_SORT_BY,
    payload: sortBy,
  };
};

export const setMaxPrice = (price) => {
  return {
    type: SET_SORT_BY,
    payload: price,
  };
};
