import {
  SET_SORT_BY,
  SET_CATEGORY,
  SET_MAX_PRICE,
} from "../constants/filterConstants";

const initialState = {
  category: "All",
  price: 0,
  sortBy: "",
};

export const filterReducer = (
  state = initialState,
  action
) => {
  switch (action.type) {
    case SET_CATEGORY:
      return {
        ...state,
        category: action.payload,
      };
    case SET_SORT_BY:
      return {
        ...state,
        sortBy: action.payload,
      };
    case SET_MAX_PRICE:
      return {
        ...state,
        price: action.payload,
      };

    default:
      break;
  }
  return state;
};
