import React from "react";
import { ArrowRightIcon } from "@heroicons/react/24/solid";
import { useDispatch } from "react-redux";
import { setCategory } from "../redux/actions/filterActions";
import categories from "../constants/categories";

const ProductsSideBar = () => {
  const dispatch = useDispatch();

  return (
    <aside class="w-full p-6 sm:w-60">
      <nav class="space-y-8 text-sm font-poppins">
        <h2 className="font-semibold text-[24px] opacity-80">
          Categories
        </h2>
        <div className="text-[18px] flex flex-col gap-1">
          {categories.map((category) => {
            <>
              <p
                onClick={() => {
                  dispatch(setCategory(categ));
                }}
                className="flex flex-row gap-2 cursor-pointer"
              >
                <ArrowRightIcon className="w-6 h-4" />
                All
              </p>
              <hr />
            </>;
          })}

          <p
            onClick={() => {
              dispatch(
                setCategory("Electronics")
              );
            }}
            className="flex flex-row gap-2 cursor-pointer"
          >
            <ArrowRightIcon className="w-6 h-4" />
            Electronics
          </p>
          <hr />
          <p
            onClick={() => {
              dispatch(setCategory("Shoes"));
            }}
            className="flex flex-row gap-2 cursor-pointer"
          >
            <ArrowRightIcon className="w-6 h-4" />
            Shoes
          </p>
          <hr />
          <p
            onClick={() => {
              dispatch(setCategory("Home Decor"));
            }}
            className="flex flex-row gap-2 cursor-pointer"
          >
            <ArrowRightIcon className="w-6 h-4" />
            Home Decor
          </p>
          <hr />
          <p className="flex flex-row gap-2 cursor-pointer">
            <ArrowRightIcon className="w-6 h-4" />
            Men
          </p>
          <hr />
        </div>
        <h4 className="font-semibold text-[24px] opacity-80">
          Price
        </h4>
        <input
          type="range"
          min="0"
          max="100"
          step="1"
          class="appearance-none h-2 w-full bg-blue-500 rounded-lg outline-none"
        />
        <button className="py-2 px-4 bg-orange-600 text-white font-normal rounded-sm">
          Clear filters
        </button>
      </nav>
    </aside>
  );
};

export default ProductsSideBar;
