import { useSelector } from "react-redux";
import {
  SET_SORT_BY,
  SET_CATEGORY,
  SET_PRICE,
  CLEAR_FILTERS,
} from "../constants/filterConstants";

function findMaxPrice(products) {
  if (products.length === 0) {
    return 0; // Return null or handle the case when the array is empty
  }

  return products.reduce(
    (maxPrice, product) => {
      if (product.price > maxPrice) {
        return product.price;
      } else {
        return maxPrice;
      }
    },
    products[0].price
  );
}

const initialState = {
  category: "All",
  price: 0,
  sortBy: "",
};

export const filterReducer = (
  state = initialState,
  action
) => {
  switch (action.type) {
    case SET_CATEGORY:
      return {
        ...state,
        category: action.payload,
      };
    case SET_SORT_BY:
      return {
        ...state,
        sortBy: action.payload,
      };
    case SET_PRICE:
      return {
        ...state,
        price: action.payload,
      };
    case CLEAR_FILTERS:
      return {
        price: 0,
        category: "All",
        sortBy: "",
      };

    default:
      break;
  }
  return state;
};
