import React, { useState } from "react";
import "./alert.css";

const Alert = ({ type, text }) => {
  const [close, setClose] = useState(false);
  return (
    <div class={`content absolute top-[75px] px-5`}>
      <div
        class={`alert ${
          type === "SUCCESS"
            ? "alert-success"
            : type === "ERROR"
            ? "alert-danger"
            : "alert-info"
        } alert-white rounded`}
      >
        <button
          onClick={() => {
            setClose(true);
          }}
          className="px-2"
          type="button"
          class="close"
          data-dismiss="alert"
          aria-hidden="true"
        >
          Ã—
        </button>
        <div class="icon">
          <i class="fa fa-check"></i>
        </div>
        <strong>
          {type === "SUCCESS"
            ? "SUCCESS!!"
            : type === "ERROR"
            ? "ERROR!!"
            : "INFO!!"}
        </strong>{" "}
        {text}
      </div>
    </div>
  );
};

export default Alert;
