import { useEffect, useState } from "react";
import {
  useLocation,
  Navigate,
} from "react-router-dom";
import axios from "../axios/axios";

const useRouteLoad = () => {
  const [redirect, setRedirect] = useState(false);
  const location = useLocation();

  useEffect(() => {
    axios
      .get("/profile")
      .then((response) => {
        if (response.data) setRedirect(true);
      })
      .catch((error) => {
        console.log(error.message);
      });
    // Code to run on each route load
    console.log("Route loaded"); // Replace with your actual code
  }, [location, redirect]);
};

export default useRouteLoad;
