import React from "react";
import "./Slider.css";

const slides = [
  {
    image: "/gadgets1",
  },
  {
    image: "/public/men-fashion",
  },
  {
    image: "/public/shoes",
  },
  {
    image: "/public/home-decor",
  },
];
const handleSlideClick = (slideNo) => {
  console.log(slideNo);
};

const Slider = () => {
  const [active, setActive] = React.useState(0);
  const [autoplay, setAutoplay] =
    React.useState(1);
  const max = slides.length;

  const intervalBetweenSlides = () =>
    autoplay &&
    setActive(
      active === max - 1 ? 0 : active + 1
    );

  React.useEffect(() => {
    const interval = setInterval(
      () => intervalBetweenSlides(),
      3000
    );
    return () => clearInterval(interval);
  });

  const nextOne = () =>
    active === max - 1
      ? setActive(0)
      : setActive((state) => state + 1);

  const prevOne = () =>
    active > 0 && setActive(active - 1);

  const setSliderStyles = () => {
    const transition = active * -100;

    return {
      width: slides.length * 100 + "vw",
      transform:
        "translateX(" + transition + "vw)",
    };
  };

  const renderSlides = () =>
    slides.map((slide, index) => (
      <div
        onClick={() => {
          handleSlideClick(index);
        }}
        className="each-slide"
        key={index}
        style={{
          backgroundImage: slide.image,
        }}
      ></div>
    ));

  const renderArrows = () => (
    <React.Fragment>
      <button
        type="button"
        className="arrows prev"
        onClick={() => prevOne()}
      >
        <svg
          fill="#FFFFFF"
          width="50"
          height="50"
          viewBox="0 0 24 24"
        >
          <path d="M15.41 7.41L14 6l-6 6 6 6 1.41-1.41L10.83 12z" />
          <path d="M0 0h24v24H0z" fill="none" />
        </svg>
      </button>
      <button
        type="button"
        className="arrows next"
        onClick={() => nextOne()}
      >
        <svg
          fill="#FFFFFF"
          height="50"
          viewBox="0 0 24 24"
          width="50"
        >
          <path d="M10 6L8.59 7.41 13.17 12l-4.58 4.59L10 18l6-6z" />
          <path d="M0 0h24v24H0z" fill="none" />
        </svg>
      </button>
    </React.Fragment>
  );

  return (
    <section className="slider">
      <div
        className="wrapper"
        style={setSliderStyles()}
      >
        {renderSlides()}
      </div>
      {renderArrows()}
    </section>
  );
};

export default Slider;
