import {
  UPDATE_PRODUCTS,
  FETCH_PRODUCTS_REQUEST,
  FETCH_ERROR,
} from "../constants/productConstants";

const initialState = {
  products: [],
  loading: false,
  error: "",
};

const productReducer = (
  state = initialState,
  action
) => {
  switch (action.type) {
    case FETCH_PRODUCTS_REQUEST:
      return { ...state, loading: true };
    case FETCH_ERROR:
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    case UPDATE_PRODUCTS:
      return {
        ...state,
        products: [
          ...state.products,
          action.payload,
        ],
        loading: false,
      };

    default:
  }
  return state;
};

export default productReducer;
