import React from "react";
import { useSelector } from "react-redux";
import ProductCard from "../components/ProductCard";
import ProductsSideBar from "../components/ProductsSideBar";

const ProductsPage = () => {
  const productsFromStore = useSelector(
    (state) => state.products.products
  );

  return (
    <section className="mt-[70px] p-10 flex flex-row px-40">
      <ProductsSideBar />
      <div className="flex flex-row">
        <input type="text" />
        <div class="relative">
  <select
    class="block appearance-none w-full py-2 px-4 pr-8 rounded-lg bg-white border border-gray-300 focus:outline-none focus:border-blue-500"
  >
    <option>Sort By</option>
    <option>Highest Price</option>
    <option>Lowest Price</option>
    <option>A-Z</option>
  </select>
  <div class="absolute inset-y-0 right-0 flex items-center pr-2 pointer-events-none">
    <svg class="h-4 w-4 text-gray-500" fill="currentColor" viewBox="0 0 20 20">
      <path
        fill-rule="evenodd"
        d="M10 12l-6-6 1.5-1.5L10 9.8l4.5-4.3L16 6z"
      />
    </svg>
  </div>
</div>

      </div>
      {/* {productsFromStore.map((product) => {
        return <ProductCard product={product} />;
      })} */}
    </section>
  );
};

export default ProductsPage;
